Date.prototype.format = function (fmt) {
    var o = {
        "M+": this.getMonth() + 1,
        "d+": this.getDate(),
        "h+": this.getHours(),
        "m+": this.getMinutes(),
        "s+": this.getSeconds(),
        "q+": Math.floor((this.getMonth() + 3) / 3),
        "S": this.getMilliseconds()
    };
    if (/(y+)/.test(fmt)) fmt = fmt.replace(RegExp.$1, (this.getFullYear() + "").substr(4 - RegExp.$1.length));
    for (var k in o)
    if (new RegExp("(" + k + ")").test(fmt)) fmt = fmt.replace(RegExp.$1, (RegExp.$1.length == 1) ? (o[k]) : (("00" + o[k]).substr(("" + o[k]).length)));
    return fmt;
};
Date.prototype.isToday = function(){
	var now = new Date();
	return now.getDate() == this.getDate() &&
		now.getYear() == this.getYear() &&
		now.getMonth() == this.getMonth();
};
Date.prototype.programTime = function(){
	var names = ['周日','周一','周二','周三','周四','周五','周六'];
	var day = this.getDay();
	var name;
	if ( this.isToday() ) name = '今天';
	else name = names[day];
	return this.format('M月d日(') + name + ')' + this.format(' hh:mm');
};
Function.prototype.append = function(){
	var fn = this;
  var obj = arguments[0];
  var splice = [].splice;
  var concat = [].concat;
  var args = splice.call(arguments, 1);
	return function(){
		var newArgs = [];
		newArgs = concat.apply(newArgs, arguments);
		newArgs = concat.apply(newArgs, args);
		return fn.apply(obj, newArgs);
	}
};
